INSERT INTO `jz_archive` VALUES ('6', '11', '19', '小卷密排丝', '0', '/uploads/20180612/3363da622ee43642b77c7b8bd3f6eb5d.jpg', '', '小卷密排丝，主要用于建筑用丝或绑丝。\r\n材料：黑铁丝，软钢丝，PVC涂塑丝，镀锌丝，不锈钢丝。', '&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;小卷密排丝，主要用于建筑用丝或绑丝。&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;材料：黑铁丝，软钢丝，PVC涂塑丝，镀锌丝，不锈钢丝。&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;在日常的生活中普遍的用到小盘丝、绑丝。低碳钢捆绑丝具有优良的耐腐蚀性和光滑的表面质量。钢丝直径范围从0.8mm至1.6mm，线规包括20＃，21＃，22＃。&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot; class=&quot;img&quot;&gt;&lt;img src=&quot;/static/index/img/small-coil-wire-1.jpg&quot; alt=&quot;&quot;/&gt;&lt;img src=&quot;/static/index/img/small-coil-wire-4.jpg&quot; alt=&quot;&quot;/&gt;&lt;img src=&quot;/static/index/img/small-coil-wire-2.jpg&quot; alt=&quot;&quot;/&gt;&lt;img src=&quot;/static/index/img/small-coil-wire-3.jpg&quot; alt=&quot;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '小卷密排丝', '小卷密排丝，主要用于建筑用丝或绑丝。\r\n材料：黑铁丝，软钢丝，PVC涂塑丝，镀锌丝，不锈钢丝。', '0', '1', '', '1528793594', '1528793594');
