INSERT INTO `jz_archive` VALUES ('20', '12', '16', 'PVC Coated Wire', '0', '/uploads/20180615/b95b150fb8f1708871c8bc66e9cea2a6.jpg', '', 'With choice galvanized iron wire as material, we offers good quality PVC coated iron wire for worldwide customers.', '&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;With choice galvanized iron wire as material, we offers good quality PVC coated iron wire for worldwide customers. PVC coated iron wire offers excellent corrosion resistance and anti-aging property and longer service life compared with common galvanized iron wire.&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;strong&gt;Materials:&lt;/strong&gt;&amp;nbsp;Low carbon steel wire, galvanized steel wire or annealed wire.&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;strong&gt;Feature:&lt;/strong&gt;&amp;nbsp;PVC Coated Iron Wire offers excellent corrosion resistance and anti-aging property and longer service life compared with common galvanized iron wire.&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;strong&gt;Sizes:&lt;/strong&gt;&amp;nbsp;0.5-4mm before coating; 1.0-5.0mm with coating layer.&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;strong&gt;Colors available:&lt;/strong&gt;&amp;nbsp;Dark green, black, white, blue, green or other colors.&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;strong&gt;Standard specification:&amp;nbsp;&lt;/strong&gt;BWG 6#-BWG 32#.&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;strong&gt;Application:&amp;nbsp;&lt;/strong&gt;The most popular use for PVC coated