INSERT INTO `jz_channel` VALUES ('14', '11', '1', '质量品控', 'quality', '', '0', '0', '质量品控', '热镀锌丝，电镀锌丝，锌铝合金铁丝', '热镀锌丝，电镀锌丝，锌铝合金铁丝', '&lt;h2 style=&quot;font-family: Arial, Verdana, sans-serif; white-space: normal;&quot;&gt;「持续改善、顾客满意、品质第一、永续经营」&lt;/h2&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; white-space: normal; color: rgb(26, 100, 161); font-weight: bold;&quot;&gt;&lt;img alt=&quot;&quot; align=&quot;right&quot; width=&quot;270&quot; height=&quot;310&quot; src=&quot;/static/index/img/about01-8.jpg&quot;/&gt;持续改善&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;石家庄建良金属制品有限公司自成立以来，即秉持品质至上，满足客户的需求为原则，并以同享成果，改善生活的品质为宗旨，以各项管理方法，随时随地对各项内部事物、外部意见执行改善工作，并以好还要更好为目标。&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; white-space: normal; color: rgb(26, 100, 161); font-weight: bold;&quot;&gt;顾客满意&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;执行以全公司整个流程从产品调查、研发、试作、生产、出售到售后服务，均致力于提供顾客在研发时效、产品品质、服务满意及价格优势下达成&amp;quot;以客为尊&amp;quot;的服务目的。&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;&lt;span style=&quot;color: rgb(26, 100, 161); font-size: 16px; font-weight: bold;&quot;&gt;品质第一&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;全公司从总经理到基层员工皆秉持&amp;quot;做好品质，人人有责&amp;quot; 的理念，并以&amp;quot;不接受不良品、不制造不良品、不产出不良品&amp;quot; 的三不政策，在产品的调查、研发设计、生产、出货及客户服务中，皆确保品质是符合客户之所求。&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; white-space: normal; color: rgb(26, 100, 161); font-weight: bold;&quot;&gt;永续经营&lt;/p&gt;&lt;p style=&quot;font-family: Arial, Verdana, sans-serif; font-size: 12px; white-space: normal;&quot;&gt;在以“以客为尊”的基本要求下，不断的作持续改善，确保品质不断提升，而顾客满意、员工满意及公司获利的基础上达成公司的企业使命。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1', '', '1528689235', '1529723494');
